import React, { useEffect, useState } from "react";
import Profile from "./components/Profile";
import Projects from "./components/Projects";
import Blog from "./components/Blog";
import Contact from "./components/Contact";

function App() {
  // --- Auth / user state ---
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [loginData, setLoginData] = useState({ username: "", password: "" });

  // --- Data from backend / CSRF ---
  const [csrfToken, setCsrfToken] = useState("");
  const [loadingUser, setLoadingUser] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    // 1) Get CSRF token
    fetch("https://localhost:3001/csrf-token", { credentials: "include" })
      .then((r) => r.json())
      .then((d) => setCsrfToken(d.csrfToken))
      .catch(() => {});

    // 2) Check current session (Google SSO)
    fetch("https://localhost:3001/auth/me", { credentials: "include" })
      .then((r) => (r.status === 200 ? r.json() : { user: null }))
      .then(({ user }) => {
        if (user) {
          setUser(user);
          setIsLoggedIn(true);
        }
        setLoadingUser(false);
      })
      .catch(() => {
        setError("Could not verify session.");
        setLoadingUser(false);
      });
  }, []);

  // Example: contact POST using CSRF
  const sendContact = async () => {
    const res = await fetch("https://localhost:3001/contact", {
      method: "POST",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
        "x-csrf-token": csrfToken,
      },
      body: JSON.stringify({ message: "Test message" }),
    });
    const data = await res.json();
    console.log(data);
  };

  // --- Fake Login Handler (demo only) ---
  // For the secure, real flow you rely on Google login.
  const handleLogin = (e) => {
    e.preventDefault();
    if (loginData.username && loginData.password) {
      setIsLoggedIn(true);
      setUser({ name: loginData.username });
      setLoginData({ username: "", password: "" });
    } else {
      alert("Please enter username and password");
    }
  };

  // --- Google SSO: start login flow ---
  const googleLogin = () => {
    window.location.href = "https://localhost:3001/auth/google";
  };

  // --- Logout (Google session) with CSRF ---
  const handleLogout = async () => {
    try {
      await fetch("https://localhost:3001/auth/logout", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
          "x-csrf-token": csrfToken, // IMPORTANT for CSRF-protected POST
        },
      });

      setIsLoggedIn(false);
      setUser(null);
    } catch (_) {}
  };

  // --- Portfolio States ---
  const [profile, setProfile] = useState({
    name: "Your Name",
    bio: "Short bio about yourself.",
    technologies: ["React", "JavaScript", "HTML", "CSS"],
  });

  const [projects, setProjects] = useState([
    {
      title: "Portfolio Website",
      description: "A personal portfolio built with React.",
      link: "https://github.com/yourusername/portfolio",
    },
  ]);

  const [blogs, setBlogs] = useState([
    {
      title: "Getting Started with React",
      content: "React is a great library for building UIs...",
    },
  ]);

  if (loadingUser) {
    return <p style={{ margin: "20px", fontFamily: "Arial" }}>Loading...</p>;
  }

  return (
    <div style={{ margin: "20px", fontFamily: "Arial" }}>
      <h1>Developer Portfolio</h1>

      {!isLoggedIn ? (
        <>
          {/* Google SSO button */}
          <button onClick={googleLogin} style={{ marginBottom: 16 }}>
            Sign in with Google
          </button>

          {/* Demo-only login form */}
          <form onSubmit={handleLogin}>
            <h2>Login (demo)</h2>
            <input
              type="text"
              placeholder="Username"
              value={loginData.username}
              onChange={(e) =>
                setLoginData({ ...loginData, username: e.target.value })
              }
              required
            />
            <br />
            <input
              type="password"
              placeholder="Password"
              value={loginData.password}
              onChange={(e) =>
                setLoginData({ ...loginData, password: e.target.value })
              }
              required
            />
            <br />
            <button type="submit">Login</button>
          </form>
        </>
      ) : (
        <>
          {/* PHASE 3: DASHBOARD SECTION */}

          <div
            style={{
              maxWidth: "700px",
              margin: "20px 0",
              background: "#fff",
              borderRadius: "8px",
              padding: "16px",
              boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
            }}
          >
            {/* Welcome message with userâ€™s name */}
            <h2>
              Welcome, {user?.displayName || user?.name}
              {user?.email ? ` (${user.email})` : ""}
            </h2>

            {/* Avatar if available */}
            {user?.photo && (
              <img
                src={user.photo}
                alt="avatar"
                width={64}
                height={64}
                style={{ borderRadius: "50%", marginTop: 8 }}
              />
            )}

            {/* User-specific details */}
            <section
              style={{
                marginTop: 16,
                padding: 12,
                background: "#fafafa",
                borderRadius: 6,
                border: "1px solid #e0e0e0",
              }}
            >
              <h3>Your Details</h3>
              <p>
                <strong>Name:</strong> {user?.displayName || user?.name}
              </p>
              {user?.email && (
                <p>
                  <strong>Email:</strong> {user.email}
                </p>
              )}
            </section>

            {/* Placeholder for future profile update form */}
            <section
              style={{
                marginTop: 16,
                padding: 12,
                borderRadius: 6,
                border: "1px dashed #9e9e9e",
                background: "#fcfcfc",
              }}
            >
              <h3>Profile Settings (Coming Soon)</h3>
              <p>
                This section will contain a profile update form in the next part
                of the project. For now, it serves as a placeholder to show
                where users will be able to edit their information.
              </p>
            </section>

            {/* Logout button */}
            <button
              onClick={handleLogout}
              style={{
                marginTop: 16,
                padding: "8px 16px",
                borderRadius: 4,
                border: "none",
                cursor: "pointer",
                background: "#e53935",
                color: "#fff",
              }}
            >
              Logout
            </button>
          </div>

          {/* Existing portfolio sections */}
          <Profile profile={profile} />
          <Projects projects={projects} />
          <Blog blogs={blogs} />
          <Contact csrfToken={csrfToken} />
        </>
      )}
    </div>
  );
}

export default App;
